package com.lamarques.testingfeatures.savecontact

import android.content.ContentValues
import android.content.Intent
import android.os.Bundle
import android.provider.ContactsContract
import android.util.Base64
import androidx.appcompat.app.AppCompatActivity
import com.lamarques.testingfeatures.databinding.ActivitySaveContactBinding

class SaveContactActivity : AppCompatActivity() {

    private lateinit var binding: ActivitySaveContactBinding

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        binding = ActivitySaveContactBinding.inflate(layoutInflater)
        setContentView(binding.root)
        supportActionBar?.title = "Save Contact"
        supportActionBar?.setDisplayHomeAsUpEnabled(true)

        binding.button.setOnClickListener {

            val data = ArrayList<ContentValues>()
            val row = ContentValues().apply {
                put(ContactsContract.Contacts.Data.MIMETYPE, ContactsContract.CommonDataKinds.Photo.CONTENT_ITEM_TYPE)
            }
/*            // Image from drawable
            val bit = ContextCompat.getDrawable(this, R.drawable.descabelado)?.toBitmap()
            val stream = ByteArrayOutputStream()
            bit?.compress(Bitmap.CompressFormat.PNG, 100, stream)
            val byteArray = stream.toByteArray()
            bit?.recycle()
            row.put(ContactsContract.CommonDataKinds.Photo.PHOTO, byteArray)
            // END - Image from drawable*/

            // Image from base64
            val encodedString = "data:image/jpeg;base64,"
            val encodedImage = encodedString.substringAfter("base64,")
            val decodedBytes = Base64.decode(encodedImage, Base64.DEFAULT)
            row.put(ContactsContract.CommonDataKinds.Photo.PHOTO, decodedBytes)
            // END - Image from base64

            data.add(row)
            saveNewContact(data)
        }
    }

    private fun saveNewContact(data: ArrayList<ContentValues>) {
        val addContactIntent = Intent(Intent.ACTION_INSERT).apply {
            type = ContactsContract.Contacts.CONTENT_TYPE
            putParcelableArrayListExtra(ContactsContract.Intents.Insert.DATA, data)
            putExtra(ContactsContract.Intents.Insert.NAME, "George Bacon")
            putExtra(ContactsContract.Intents.Insert.PHONE_TYPE, "Home")
            putExtra(ContactsContract.Intents.Insert.PHONE, "1133333333")
            putExtra(ContactsContract.Intents.Insert.SECONDARY_PHONE_TYPE, "Mobile")
            putExtra(ContactsContract.Intents.Insert.SECONDARY_PHONE, "11123456789")
            putExtra(ContactsContract.Intents.Insert.TERTIARY_PHONE_TYPE, "Other")
            putExtra(ContactsContract.Intents.Insert.TERTIARY_PHONE, "1199999999")
            putExtra(ContactsContract.Intents.Insert.EMAIL_TYPE, "Comercial")
            putExtra(ContactsContract.Intents.Insert.EMAIL, "teste@teste.com")
            putExtra(ContactsContract.Intents.Insert.COMPANY, "Company")
            putExtra(ContactsContract.Intents.Insert.JOB_TITLE, "Job Title")
        }

        startActivity(addContactIntent)
    }
}